    DECLARE new_score INT;

    -- 조건: 대기 → 완료로 바뀐 경우에만 실행
    IF OLD.status = '대기' AND NEW.status = '완료' THEN

        -- report_score +1 (NULL이면 0 처리)
        UPDATE member
        SET report_score = IFNULL(report_score, 0) + 1
        WHERE member_id = NEW.reported_member_id;

        -- 증가된 score 가져오기
        SELECT report_score INTO new_score
        FROM member
        WHERE member_id = NEW.reported_member_id;

        -- 점수에 따라 suspended_until 설정
        IF new_score = 3 THEN
            UPDATE member
            SET suspended_until = DATE_ADD(NOW(), INTERVAL 3 DAY)
            WHERE member_id = NEW.reported_member_id;
        ELSEIF new_score = 5 THEN
            UPDATE member
            SET suspended_until = DATE_ADD(NOW(), INTERVAL 5 DAY)
            WHERE member_id = NEW.reported_member_id;
        ELSEIF new_score = 7 THEN
            UPDATE member
            SET suspended_until = DATE_ADD(NOW(), INTERVAL 7 DAY)
            WHERE member_id = NEW.reported_member_id;
        ELSEIF new_score >= 10 THEN
            UPDATE member
            SET suspended_until = '9999-12-31'
            WHERE member_id = NEW.reported_member_id;
        END IF;

    END IF;
END//
DELIMITER ;
SET SQL_MODE=@OLDTMP_SQL_MODE;

