-- 트리거 eatometer.trg_update_restaurant_rating 구조 내보내기
SET @OLDTMP_SQL_MODE=@@SQL_MODE, SQL_MODE='ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION';
DELIMITER //
CREATE TRIGGER `trg_update_restaurant_rating` AFTER INSERT ON `review` FOR EACH ROW BEGIN
    DECLARE avg_rating DECIMAL(3,2);

    -- 평균 평점 계산 (해당 식당의 전체 리뷰를 기준으로)
    SELECT ROUND(AVG(rating), 2)
    INTO avg_rating
    FROM review
    WHERE rst_id = NEW.rst_id;

    -- 식당 테이블의 평점 갱신
    UPDATE restaurant
    SET rating = avg_rating
    WHERE rst_id = NEW.rst_id;
END//
DELIMITER ;
SET SQL_MODE=@OLDTMP_SQL_MODE;

-- 트리거 eatometer.update_status_on_endDate_update 구조 내보내기
SET @OLDTMP_SQL_MODE=@@SQL_MODE, SQL_MODE='ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_ENGINE_SUBSTITUTION';
DELIMITER //
CREATE TRIGGER `update_status_on_endDate_update` BEFORE UPDATE ON `event` FOR EACH ROW BEGIN
  IF NEW.endDate < CURDATE() THEN
    SET NEW.status = '종료';
  ELSE
    SET NEW.status = '진행중';
  END IF;
END//
DELIMITER ;
